<?php if (!defined('BASEPATH')) exit('No direct script access allowed');class Web_auth_model extends CI_Model{  public function __construct()  {    parent::__construct();  }  public function checkLogin()  {      $username = trim($this->input->post('username'));      $password = trim($this->input->post('password'));      $this->db->select('*');      $this->db->from('accounts');      $this->db->where('username',$username);      $this->db->where('password',$password);      $query = $this->db->get();      $results = $query->result_array();      if(count($results) > 0) {          $session_array = array('web_user_login' => $results[0]);          $this->session->set_userdata($session_array);          return true;      }      return false;  }    }